insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES (2, 25,'Scott', 'Derrickson', '1966-07-16', 2,'Scott_Derrickson.jpg','Скотт Дерриксон родился и вырос в Денвере, окончил университет Байона со степенью бакалавра по литературе, философии, и в области коммуникаций, получил степень магистра в "USC School of Cinematic ArtsДебютом в качестве сценариста в большом кино для Дерриксона стал слэшер "Городские легенды 2: Последний отрезок", а в качестве постановщика  — пятая часть культовой франшизы, фильм ужасов «Hellraiser: Inferno».Самые известные и коммерчески успешные режиссерские работы Дерриксона: "Шесть демонов Эмили Роуз", "День, когда Земля остановилась", "Синистер"')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES (2, 26,'Taika', 'Waititi', '1975-08-16', 2,'Taika_Waititi.jpg','Тайка Дэвид Вайтити родился 16 августа 1975 года в Веллингтоне. Отец — маори, мать — еврейка. Вайтити окончил Университет королевы Виктории в Веллингтоне. Вместе с Джемейном Клементом образовал комедийный дуэт «The Humourbeasts». Дебютировал в кино в 1999 году. С 1999 по 2004 год снимался под псевдонимом Тайка Коэн (девичья фамилия матери). Получил известность благодаря снятой короткометражке «Две машины, одна ночь», а также ролям в фильмах «Орёл против Акулы», «Мальчик», «Зелёный фонарь» и «Реальные упыри». В 2017 году вышел фильм Вайтити «Тор: Рагнарёк».')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES (2, 27,'Anthony', 'Russo', '1970-02-03', 2,'Anthony_Russo.jpg','Энтони Руссо родился 3 февраля 1970 года в Кливленде, штат Огайо, США, как Энтони Дж. Руссо. Он является продюсером и режиссером, известным по фильмам «Мстители: Бесконечная война» (2018 г.), « Капитан Америка: Le soldat de lhiver» (2014 г.) и « Капитан Америка: гражданская война» (2016 г.).')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES (2, 28,'Joseph', 'Russo', '1971-07-08', 2,'Joseph_Russo.jpg','Американский режиссер кино и телевидения, лауреат «Эмми», сценарист, монтажер, продюсер, актер Джо Руссо родился 8 июля 1971 года в Кливленде, штат Огайо, США. Мать – Патрисия Руссо – адвокат. Отец – Бэзил Руссо – судья. Старший брат – Энтони Руссо. Братья Руссо вместе снимают почти все свои фильмы. Младшая сестра – Анджела Руссо-Остот – сценарист и продюсер сериалов, работает на телевидении. Джо вырос в Кливленде, учился в Бенедиктинской средней школе. В отличие от старшего брата, закончил университет Айовы, где был успешен в английском и письме. Затем поступил в университет Кейс Вестерн.')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES (2, 29,'Zack', 'Snyder', '1966-03-01', 2,'Zack_Snyder.jpg','Зак Снайдер родился 1 марта 1966 года в Грин-Бей, штат Висконсин, вырос в Гринвич, штат Коннектикут. Учился в школе Дэйкрофт, где преподавала его мать — учитель живописи и фотографии. Благодаря матери, которая вдохновила его изучать живопись, Снайдер получил художественное образование в Лондоне и Пасадине. Снайдер до сих пор живёт в Пасадине со своей второй женой продюсером Деборой Снайдер, у них 6 детей. С юных лет Зак Снайдер испытывал страсть к фантастическим фильмам и рассказам. Особое влияние на становление его вкусов и пристрастий в области кино оказала сага Джорджа Лукаса «Звёздные войны». Видя страстное увлечение сына кинематографом, родители решили ему подарить видеокамеру. Это была обычная восьмимиллиметровая «игрушка», именно с ней Зак начал делать свои первые шаги в режиссуре.')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES (2, 30,'Frank', 'Darabont', '1959-01-28', 2,'Frank_Darabont.jpg','Американский кинорежиссёр, сценарист и продюсер венгерского происхождения. Был трижды номинирован на «Оскар». Известен своими удачными экранизациями романов Стивена Кинга: «Побег из Шоушенка», «Зелёная миля», «Мгла» и другие.')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES (2, 31,'Scott', 'Speer', '1982-06-02', 2,'Scott_Speer.jpg','Шпеер родился в Сан-Диего , штат Калифорния . В 23 года он был подписан на HSI Productions , а в 2006 году Шпир получил награду MVPA за режиссерский дебют года за музыкальное видео « Звезды » от Switchfoot .')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES (2, 32,'James', 'Cameron', '1954-08-16', 1,'James_Cameron.jpg','Канадский кинорежиссёр, сценарист, продюсер, монтажёр, океанограф.')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES (2, 33,' Jon', 'Turteltaub', '1963-08-06', 1,'Jon_Turteltaub.jpg','американский кинорежиссёр и продюсер.')
