insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES (2, 35,'Guy', 'Ritchie', '1968-09-10', 1,'Guy_Ritchie.jpg','британский кинорежиссёр, сценарист, продюсер. Наиболее известные фильмы — «Карты, деньги, два ствола», «Большой Куш», «Револьвер», «Рок-н-рольщик», «Агенты А.Н.К.Л.», «Меч короля Артура» и дилогия «Шерлок Холмс».')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (1, 'Men in Black: International', 'MIB.jpg', '2018-06-12', 0, 5.45, 120, 'Люди в черном, тайная организация на страже покоя и безопасности Земли, уже не раз защищали нас от нападения отбросов Вселенной. На этот раз самая большая опасность для мирового сообщества, которой агентам предстоит противостоять — шпион в их рядах.')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (2, 'Inception', 'inception.jpg', '2018-06-12', 0, 5.45, 120, 'Кобб — талантливый вор, лучший из лучших в опасном искусстве извлечения: он крадет ценные секреты из глубин подсознания во время сна, когда человеческий разум наиболее уязвим. Редкие способности Кобба сделали его ценным игроком в привычном к предательству мире промышленного шпионажа, но они же превратили его в извечного беглеца и лишили всего, что он когда-либо любил.')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (3, 'Thor 3: Ragnarok', 'thor_3.jpg', '2018-06-12', 0, 5.45, 120, 'Вернувшись в Асгард в поисках таинственного врага, ведущего охоту на Камни Бесконечности, Тор обнаруживает, что действия его брата Локи, захватившего трон Асгарда, привели к приближению наиболее страшного события — Рагнарёка. По легенде, это ознаменует последнюю битву Асгарда, последствием которой станет его полное уничтожение. ')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (4, 'Avengers', 'avengers_3.jpg', '2018-06-12', 0, 5.45, 120, 'Оставшиеся в живых члены команды Мстителей и их союзники должны разработать новый план, который поможет противостоять разрушительным действиям могущественного титана Таноса. После наиболее масштабной и трагической битвы в истории они не могут допустить ошибку.')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (5, 'Green Mile', 'green_mile.jpg', '2018-06-12', 0, 5.45, 120, 'Обвиненный в страшном преступлении, Джон Коффи оказывается в блоке смертников тюрьмы «Холодная гора». Вновь прибывший обладал поразительным ростом и был пугающе спокоен, что, впрочем, никак не влияло на отношение к нему начальника блока Пола Эджкомба, привыкшего исполнять приговор.')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (6, 'Interstellar', 'Interstellar.jpg', '2018-06-12', 0, 5.45, 120, 'Наше время на Земле подошло к концу, команда исследователей берет на себя самую важную миссию в истории человечества; путешествуя за пределами нашей галактики, чтобы узнать есть ли у человечества будущее среди звезд.')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (7, 'Midnight sun', 'midnight_sun.jpg', '2018-06-12', 0, 5.45, 120, 'Ночами 17-летняя Кэти сочиняет свои красивые песни под гитару, а днем вынуждена скрываться во мраке: её нежная кожа не выносит солнечного света. Но однажды в полночь она знакомится с Чарли, веселым парнем с копной рыжих, как солнце, волос и очаровательной улыбкой. ')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (8, 'Man of steel', 'Man_of_Steel.jpg', '2018-06-12', 0, 5.45, 120, 'Кларк Кент/Кал-Эл — молодой журналист, который чувствует себя чужаком из-за своей невероятной силы. Много лет назад он был отправлен на Землю с развитой планеты Криптон, и теперь задается вопросом: зачем?')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (9, 'Doctor Strange', 'Doctor_Strange_poster.jpg', '2018-06-12', 0, 5.45, 120, 'Страшная автокатастрофа поставила крест на карьере успешного нейрохирурга Доктора Стрэнджа. Отчаявшись, он отправляется в путешествие в поисках исцеления и открывает в себе невероятные способности к трансформации пространства и времени.')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (10, 'The Terminator', 'terminator-1984.jpg', '1984-10-26', 0, 5.45, 107,  'История противостояния солдата Кайла Риза и киборга-терминатора, прибывших в 1984-й год из пост-апокалиптического будущего, где миром правят машины-убийцы, а человечество находится на грани вымирания. Цель киборга: убить девушку по имени Сара Коннор, чей ещё нерождённый сын к 2029 году выиграет войну')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (11, 'Terminator 2: Judgment Day', 'terminator-2-sudnyy-den-1991.jpg', '1991-07-01', 0, 5.45, 153, 'Прошло более десяти лет с тех пор, как киборг-терминатор из 2029 года пытался уничтожить Сару Коннор — женщину, чей будущий сын выиграет войну человечества против машин.')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (12, 'Aliens', 'chuzhie-1986.jpg', '1986-07-18', 0, 5.45, 154, ' научно-фантастический фильм ужасов[2] режиссёра Ридли Скотта. Название отсылает к главному антагонисту фильма — Чужому, чрезвычайно агрессивному инопланетному существу, которое выслеживает и убивает экипаж космического корабля. Премьера фильма в США состоялась 25 мая 1979 года')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (13, 'Titanic', 'titanik-1998.jpg', '1998-02-20', 0, 5.45, 194,'американский фильм-катастрофа 1997 года, снятый режиссёром Джеймсом Кэмероном, в котором показана гибель легендарного лайнера «Титаник». Герои фильма, будучи членами различных социальных слоёв, влюбились друг в друга на борту лайнера, совершавшего свой первый и последний рейс через Атлантический океан в 1912 году.')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (14, 'Avatar', 'avatar-2009.jpg', '2009-12-17', 0, 5.45, 178, 'Джейк Салли — бывший морской пехотинец, прикованный к инвалидному креслу. Несмотря на немощное тело, Джейк в душе по-прежнему остается воином. Он получает задание совершить путешествие в несколько световых лет к базе землян на планете Пандора, где корпорации добывают редкий минерал, имеющий огромное значение для выхода Земли из энергетического кризиса.')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (15, 'National Treasure', 'sokrovische-nacii-2004.jpg', '2004-12-30', 0, 5.45, 131, ' американский приключенческий фильм 2004 года режиссёра Джона Тартелтауба, компании Walt Disney Pictures. Действие фильма происходит в США, где в поисках затерянного сокровища главные герои похищают Декларацию независимости США, обнаруживают ключи и подсказки, ведущие их по пути тамплиеров и масонов.')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (16, 'National Treasure: Book of Secrets', 'sokrovische-nacii-kniga-tayn-2007.jpg', '2007-12-27', 0, 5.45, 124, '«Сокро́вище на́ции: Кни́га тайн» — американский приключенческий фильм 2007 года компании Walt Disney Pictures. Является продолжением фильма «Сокровище нации», вышедшего в 2004 году. Мировая премьера состоялась 21 декабря 2007')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (17, 'The Sorcerers Apprentice', 'uchenik-charodeya.jpg', '2007-07-15', 0, 5.45, 109,'«Учени́к чароде́я» — фэнтезийный приключенческий фильм 2010 года, спродюсированный Джерри Брукхаймером, режиссёра Джона Тёртелтауба с Николасом Кейджем в главной роли. Дистрибьютором фильма является Walt Disney Pictures.')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (18, 'The MEG', 'meg-monstr-glubiny.jpg', '2018-08-09', 0, 5.45, 113, 'Профессор палеонтологии Джонас Тэйлор, в прошлом пилот глубоководных аппаратов, уже много лет пишет о мегалодоне и читает лекции, стараясь забыть незабываемое — трагически окончившуюся экспедицию, которая убедила его в том, что первобытное чудовище существует и в наши дни. ')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (19, 'Gone in Sixty Seconds', 'ugnat-za-60-sekund-2000.jpg', '2000-05-15', 0, 5.45, 115,'Что можно успеть за 60 секунд? Выкурить сигарету или выпить чашку кофе. Но только один профессионал в мире, легендарный вор по кличке Мемфис, может за считанные секунды угнать любой автомобиль.')
insert into films(id, title, image_Small_Path, release_Date, views, rating, film_Length, description) VALUES (20, 'Lock, Stock and Two Smoking Barrels', 'karty-dengi-dva-stvola-1998.jpg', '1998-07-27', 0, 5.45, 107,'Четверо молодых парней накопили каждый по 25 тысяч фунтов, чтобы один из них мог сыграть в карты с опытным шулером и матерым преступником, известным по кличке Гарри-Топор. Парень в итоге проиграл 500 тысяч, на уплату долга ему дали неделю.')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES(1, 1, 'Chris', 'Hemsworth', '1983-08-11', 2, 'prat.jpg', 'Крис Хемсворт родился в 1983 году в Мельбурне. Его братья, Люк и Лиам, тоже актёры. Когда Хемсворту было 14 лет, он и его семья переехали на маленький остров Филипп. В марте 2009 года Хемсворт переехал в Соединенные Штаты, чтобы продолжить свою карьеру там. Он и его брат Лиам остались в гостевом доме агента Криса Уильяма Ворда, прежде чем арендовать свою собственную квартиру в Лос-Анджелесе, где они проживают в настоящее время.')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES(1, 2, 'Scarlett', 'Johansson', '1984-11-22', 2,'Scarlett_Johansson.jpg','Американская актриса кино и телевидения, певица. Наибольшую известность ей принесло сотрудничество с Вуди Алленом и Софией Копполой, а также роль Чёрной вдовы в серии фильмов кинематографической вселенной Marvel.')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES(1, 3, 'Chris', 'Evans', '1981-07-13', 2,'Chris_Evan.jpg','Американский актёр. Мировую известность ему принесла роль Капитана Америки в кинематографической вселенной Marvel. Эванс также исполнил роль Человека-факела в фильме «Фантастическая четвёрка» и его сиквеле 2007 года.')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES(1, 4, 'Leonardo', 'DiCaprio', '1974-11-11', 2,'Leonardo_DiCaprio.jpg','Американский актёр и продюсер. Четыре раза был номинирован на премию «Оскар» за лучшую мужскую роль в фильмах «Авиатор», «Кровавый алмаз», «Волк с Уолл-стрит» и «Выживший», а также за лучшую мужскую роль второго плана в фильме «Что гложет Гилберта Грэйпа».')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES(1, 5, 'Tessa', 'Thompson', '1983-10-03', 2,'Tessa_Thompson.jpg','Американская актриса. Её прорывом стала роль в независимом фильме «Миссисипи проклятых». Наибольшую известность ей принесли роли в фильмах «Песни о любви», «Уважаемые белые люди», «Сельма», «Крид: Наследие Рокки» и его сиквеле «Крид 2», «Тор: Рагнарёк» и «Аннигиляция».')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES(1, 6, 'Benedict', 'Cumberbatch', '1976-07-19 ', 2,'Benedict_Cumberbatch.jpg','Британский актёр театра, кино и телевидения. Лауреат премии Лоренса Оливье и «Эмми», двукратный номинант на премию «Золотой глобус», номинант на премию «Оскар». Командор Ордена Британской империи.')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES(1, 7, 'Henry', 'Cavill', '1983-05-05 ', 2,'Henry_Cavill.jpg','Английский актёр. Известен по ролям в фильмах «Война богов: Бессмертные», «Средь бела дня», «Человек из стали», «Агенты А.Н.К.Л.», «Лига справедливости» и в сериале «Тюдоры». ')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES(1, 8, 'Amy', 'Adams', '1974-08-20 ', 2,'Amy_Adams.jpg','Американская актриса и певица, шестикратная номинантка на премию «Оскар». Обладательница двух премий «Золотой глобус» за главную роль в трагикомедии «Афера по-американски» и за главную роль в драме «Большие глаза».')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES(1, 9, 'Tom', 'Hanks', '1956-07-09 ', 2,'Tom_Hanks.jpg','Хэнкс начинал свою актёрскую карьеру с ролей на телевидении и комедийных фильмов, прежде чем добился признания в качестве драматического актёра, и получил две премии «Оскар» в категории «Лучшая мужская роль» — за главные роли в фильмах «Филадельфия» (1993) и «Форрест Гамп» (1994), тем самым став одним из двух актёров в истории кинематографа (помимо Спенсера Трейси), выигравших данную награду два раза подряд.')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES(1, 10, 'Joseph', 'Gordon-Levitt', '1981-02-17 ', 2,'Joseph_Gordon-Levitt.jpg','Джозеф Гордон-Левитт родился 17 февраля 1981 года в Лос-Анджелесе (Калифорния, США) в еврейской семье, в которой не придерживались строгих религиозных традиций. Его отец, Деннис Левитт, работал на радиостанции Pacifica Radio, а мать, Джейн Гордон, была редактором программы телепередач на той же самой радиостанции. У Джо был старший брат Дэниэл, который скончался в 2010 году.')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES(1, 11, 'Bella', 'Thorne', '1997-10-8 ', 2,'Bella_Thorne.jpg','Белла Торн родилась 8 октября 1997 года во Флориде. Она младший ребёнок в семье Рея и Тамары Торнов. У неё две старшие сестры — Дани и Кайли — и старший брат Реми, которые также являются актёрами. Будучи шестинедельной малышкой, Белла снялась для каталога «Parents Magazine», с чего и началась её карьера модели. Спустя несколько лет Белла начала сниматься в фильмах и сериалах.')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES(1, 12, 'Matthew', 'McConaughey', '1969-11-04', 2,'Matthew_McConaughey.jpg','Американский актёр, кинорежиссёр, сценарист и продюсер. Поначалу, зарекомендовав себя как актёра в основном комедийного амплуа, во втором десятилетии XXI века Макконахи перешёл к крупным драматическим ролям, удостоившись ряда наград и положительных отзывов от кинопрессы за картины «Линкольн для адвоката», «Мад», «Киллер Джо», «Супер Майк», «Интерстеллар».')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES(1, 13, 'Arnold', 'Schwarzenegger', '1947-07-30', 1,'Arnold_Schwarzenegger.jpg','Американский культурист, предприниматель и актёр австрийского происхождения, политик-республиканец, 38-й губернатор Калифорнии.')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES(1, 14, 'Nicolas', 'Cage', '1964-01-07', 1,'Nicolas_Cage.jpg','Профессионально известный как Ни́колас Кейдж (англ. Nicolas Cage), — американский актёр, режиссёр и продюсер. Лауреат премий «Оскар» и «Золотой глобус».')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES(1, 15, 'Sigourney', 'Weaver', '1949-10-08', 1,'Sigourney_Weaver.jpg','Американская актриса и продюсер. Приобрела широкую известность после исполнения роли Эллен Рипли, в фильмах «Чужой» (1979), «Чужие» (1986), «Чужой 3» (1992) и «Чужой: Воскрешение» (1997).')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES(1, 16, 'Sam', 'Worthington', '1976-08-02', 5,'Sam_Worthington.jpg','Австралийский актёр родом из Англии. Всемирную известность приобрёл благодаря главным ролям в фильмах «Аватар», «Битва Титанов» и его продолжения «Гнев титанов», «Терминатор: Да придёт спаситель», «Прошлой ночью в Нью-Йорке», «На грани» и «По соображениям совести».')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES(1, 17, 'Jason', 'Statham', '1967-07-26', 1,'Jason_Statham.jpg','английский актёр, известный по фильмам режиссёра Гая Ричи «Карты, деньги, два ствола», «Большой куш» и «Револьвер», а также дилогии «Адреналин» и сериям фильмов «Перевозчик» (англ.)русск., «Неудержимые» и «Форсаж»')
insert into Personalities (personality_position, id, first_Name, last_Name, date_Of_Birthday, country_id, photo, biography) VALUES(1, 18, 'Angelina', 'Jolie', '1975-06-04', 1,'Angelina_Jolie.jpg','американская актриса, кинорежиссёр и сценаристка, фотомодель, посол доброй воли ООН.')
